#
# This is the server logic of the Income Explorer Shiny web application. You can
# run the application by clicking 'Run App' above.
#

suppressMessages({
  library(shiny)
  library(openxlsx)
  library(rhandsontable)
  library(magrittr)
  library(data.table)
})

# Define server logic required to run the app
shinyServer(function(input, output, session) {
  ##########################################
  # Pop up warning on start 
  ##########################################
  # the modal dialog where the user can enter the query details.
  warning_modal <- modalDialog(
    title = "Warning", 
    paste0(
      "This software is provided as-is, for research purposes only, ",
      "with absolutely no warranty or guarantee of correctness."
    ),
    easyClose = FALSE, fade = FALSE
  )
  
  # Show the model on start up ...
  showModal(warning_modal)
  
  ##########################################
  # Loading data and calculating incomes 
  ##########################################
  
  WEEKS_IN_YEAR <- 52L
  
  # Read in the parameters from files
  reload_data <- reactive({
    if(!is.null(input$show_parameters)){
      DF <- hot_to_r(input$show_parameters)
      parameters_SQ <- parameters_from_df(DF, parameters_column = 2)
    } else {
      parameters_SQ <- get_parameters()
    }
    
    return(list(
      parameters_SQ = parameters_SQ
    ))
  })
  
  
  # calculate incomes 
  calculate_income <- reactive({
    parameters = req(reload_data()) # req
    
    # convert inputs
    MAX_WAGE <- 50*input$wage1_hourly 
    children <- convert_ages(input$Children_ages)
    
    if (input$Acc_type == "Renting"){
      AS_Accommodation_Rent <- TRUE
    } else {
      AS_Accommodation_Rent <- FALSE
    }
    
    
    if (input$Partnered == 1){
      if (!is.na(input$hours2)) {
        partner_hours <- input$hours2
      } else {
        partner_hours <- 0
      }
      
      if (!is.na(input$gross_wage2)) {
        partner_wages <- input$gross_wage2*input$hours2
      } else {
        partner_wages <- 0
      }
    } else {
      partner_wages <- 0
      partner_hours <- 0
    }
    
    # Create helper emtr function using current inputs
    hot_emtr <- function(params) {
      emtr_df <- emtr(
        # System parameters
        params,
        # Family parameters
        input$Partnered, input$wage1_hourly, children, partner_wages, partner_hours,
        input$AS_Accommodation_Costs, AS_Accommodation_Rent, as.numeric(input$AS_Area),
        # Presentation parameters
        max_wage = MAX_WAGE, steps_per_dollar = 1L, weeks_in_year = WEEKS_IN_YEAR, 
        MFTC_WEP_scaling = as.numeric(input$MFTC_WEP_scaling)
      )
      return(emtr_df)
    }
    
    X_SQ <- hot_emtr(parameters$parameters_SQ)
    
    # MFTC is meant to make families always better off being off-benefit than staying
    # on a benefit. We let the user set whether the family stays on benefit
    # or gets IWTC when they work, with the parameter input$WFFBEN_SQ. This can be:
    # "Max" - choose the option which maximises the family income.
    # "WFF" - go off the benefit while working, and get IWTC + MFTC.
    # "Benefit" - stay on the benefit while working, and never get IWTC or MFTC
    #             (benefit abates away as earned income increases).
    # Note that these are only applicable when beneficiaries are ineligible for IWTC;
    # MFTC eligibility currently depends on IWTC eligibility in the `emtr` function.
    
    if (input$WFFBEN_SQ != "WFF") {
      SQ_params_with_no_IWTC <- remove_IWTC_from_params(parameters$parameters_SQ)
      X_SQ_without_IWTC <- hot_emtr(SQ_params_with_no_IWTC)

      if (input$WFFBEN_SQ == "Max") {
        # Choose which of benefit or IWTC gives max net income
        X_SQ <- choose_IWTC_or_benefit(X_SQ, X_SQ_without_IWTC)

      } else if (input$WFFBEN_SQ == "Benefit") {
        X_SQ <- X_SQ_without_IWTC
      }
    }
    
    # used to ensure that SQ and reform plots have the same axes
    max_income <- max(X_SQ$gross_wage1_annual)
    
    max_net_income <- 1.1*WEEKS_IN_YEAR*max(
      max(X_SQ$Net_Income)
    )
    min_y_SQ <- X_SQ[, WEEKS_IN_YEAR*min(
      -(
        gross_benefit1 + gross_benefit2 - net_benefit1 - net_benefit2 +
          wage1_tax + wage2_tax + wage1_ACC_levy + wage2_ACC_levy
      )
    )]

    min_y <- 1.1*min(min_y_SQ) 
    
    return(list(
      X_SQ = X_SQ,
      max_income = max_income,
      max_net_income = max_net_income, 
      min_y = min_y
    ))
  })
  
  ########################################## 
  ### EMTR Tab
  ########################################## 
  
  output$plot_budgetconstraint_SQ<- renderPlotly({
    compElements = c("Best Start", "Winter Energy", "Accomodation Supplement", "IWTC", "FTC",
                     "MFTC", "IETC", "Net Core Benefit", "Net Wage", "Tax on Core Benefit", "Tax on Wage and ACC")
    X_results <- req(calculate_income())
    X_SQ <- X_results$X_SQ
    amounts_net_plot(
      X_SQ,
      inc_limit = X_results$max_income,
      y_min = X_results$min_y,
      y_max = X_results$max_net_income,
      display_cols = FALSE)
  })
  
  output$plot_incomecomposition_SQ<- renderPlotly({
    compElements = c("Best Start", "Winter Energy", "Accomodation Supplement", "IWTC", "FTC",
                     "MFTC", "IETC", "Net Core Benefit", "Net Wage", "Tax on Core Benefit", "Tax on Wage and ACC")
    X_results <- req(calculate_income())
    X_SQ <- X_results$X_SQ
    amounts_net_plot(
      X_SQ,
      inc_limit = X_results$max_income,
      y_min = X_results$min_y,
      y_max = X_results$max_net_income,
      display_cols = TRUE)
  })
  
  # emtr plots
  output$plot_emtr<- renderPlotly({
    reload_data()
    X_results <- req(calculate_income())
    
    X_SQ <- X_results$X_SQ
    
    compare_plots(X_SQ, type = "EMTR", min_rate = 0, max_rate = 1.1,
      inc_limit = X_results$max_income, title = "EMTR comparison",
      policy_name1 = 'Status Quo', policy_name2 = 'Reform',
      watermark = FALSE, weeks_in_year = WEEKS_IN_YEAR
    )
  })
  
  ########################################## 
  ### Outputs
  ##########################################
  
  # NOTE: Download not available in shinylive
  
  # Download everything 
  output$downloadData <- downloadHandler(
    filename = function() {
      "IncomeExplorerResults.xlsx"
    },
    content = function(file) {
      X_results <- calculate_income()
      parameters = reload_data()
      
      wb <- createWorkbook()
      
      # Details of the example family and input files
      details <- c(
        SQ_file = input$parameters_SQ$name,
        HourlyWage = input$wage1_hourl,
        Partnered = input$Partnered,
        Partner_HourlyWage = input$gross_wage2*(input$Partnered == 1),
        Partner_HoursWorked = input$hours2*(input$Partnered == 1),
        Accomodation_Costs = input$AS_Accommodation_Costs,
        Accomodation_Type = input$Acc_type,
        AS_Area = input$AS_Area,
        Children_Ages = input$Children_ages
      )
      
      addWorksheet(wb, 'Details')
      writeData(wb, 'Details', names(details), startCol=1)
      writeData(wb, 'Details', details, startCol=2)
      
      # Full sets of results (should probably be more selective)
      addWorksheet(wb, 'SQ')
      writeData(wb, 'SQ', X_results$X_SQ)
      
      saveWorkbook(wb, file, overwrite = TRUE)
    }
  )
})

DEFAULT_MIN_WAGES <- c(
  "2014" = 13.75,
  "2015" = 14.25,
  "2016" = 14.75,
  "2017" = 15.25,
  "2018" = 15.75,
  "2019" = 16.50,
  "2020" = 17.70,
  "2021" = 18.90,
  "2022" = 20.00,
  "2023" = 21.20,
  "2024" = 22.70,
  # Assumes the minimum wage is unchanged for future tax years
  "2025" = 22.70,
  "2026" = 22.70,
  "2027" = 22.70
)

estimate_MFTC_from_files <- function(
  parameter_files,
  min_wages = DEFAULT_MIN_WAGES,
  WEP_scaling = 1
) {
  estimated_MFTC_rates <- data.table()
  for (parameter_file in parameter_files){
    tax_year <- parameter_file %>% basename() %>%
      stringr::str_extract("TY[0-9]{2}") %>%
      stringr::str_remove("TY") %>% as.numeric()
    tax_year <- tax_year + 2000
    if (tax_year > 2023) {
      logging::logwarn(
        "Minimum wage for tax year %d is not legislated yet, subject to change",
        tax_year
      )
    }
    if (tax_year %in% names(min_wages)) {
      min_wage <- min_wages[[as.character(tax_year)]]
    } else {
      stop("Minimum wage not defined for tax year ", tax_year)
    }
    this_MFTC_rates <- estimate_MFTC_rates(
      parameter_file,
      min_wage = min_wage,
      steps_per_dollar = 52L,
      WEP_scaling = WEP_scaling
    )
    
    estimated_MFTC_rates <- rbind(estimated_MFTC_rates, this_MFTC_rates, fill = TRUE)
  }
  
  estimated_MFTC_rates[, max_MFTC := max(MFTC_minincome), by = File]
  return(estimated_MFTC_rates)
}

# Helper functions for EMTR function

#' @title Abatement
#' @author Christopher Ball, \email{Christopher.Ball@@treasury.govt.nz}
#'
#' @description Works out the abatement on a given amount under a given abatement regime
#' against a certain income.  Can be applied only to certain sample elements using
#' the Cond argument, and is fully vectorised by design.
#'
#' @param Cond Boolean vector of which elements are abated.  Can use
#' \code{T} to apply to all elements.
#' @param Amount The amount which is abated.
#' @param Scale An abatement scale (set of rates and thresholds) which is applied to
#' the amount.
#' @param Income An income measure used for determining abatement
#' @return Vector of abated incomes.  Assumes that abated amount is non-negative.
#' @family Utilities
#' @export
Abate <- function(Cond, Amount, Scale, Income){
  Scale_Rows <- nrow(Scale)
  for (Row in 2:Scale_Rows){
    Amount <- Amount - Cond * pmax(0, pmin(Scale[Row, 1], Income) - Scale[Row - 1, 1]) * Scale[Row - 1, 2]
  }
  Amount <- Amount - Cond * pmax(0, Income - Scale[Scale_Rows, 1]) * Scale[Scale_Rows, 2]
  return(pmax(0, Amount))
}
attr(Abate, "utility") <- T

#' @title Abatement Vanishing Point
#' @author Christopher Ball, \email{Christopher.Ball@@treasury.govt.nz}
#'
#' @description Works out the minimum income amount under a given scale at which
#' the abated amount is $0.  Assumes that such a point exists.  Not vectorised,
#' but it doesn't in general need to be as a vector can be made through recycling.
#'
#' @inheritParams Abate
#' @return Abatement vanishing point.
#' @family Utilities
#' @export
Abatement_Vanishing_Point <- function(Scale, Amount){
  # Not vectorised - but it doesn't need to be!
  n <- nrow(Scale)
  for (Th in 1:(n-1)){
    if (Scale[Th, 2] == 0) next
    if (Scale[Th, 1] + Amount/Scale[Th, 2] > Scale[Th + 1, 1]){
      Amount = Amount - Scale[Th, 2]*(Scale[Th + 1, 1] - Scale[Th, 1])
    } else {
      return(Scale[Th, 1] + Amount/Scale[Th, 2])
    }
  }
  return(Scale[n, 1] + Amount/Scale[n, 2])
}
attr(Abatement_Vanishing_Point, "utility") <- T

#' @title Applies a given tax system to an income amount.
#' @author Christopher Ball, \email{Christopher.Ball@@treasury.govt.nz}
#'
#' @description This function calculates the tax amount due given the pre-tax
#' amount and the relevant tax scale.  Vectorised for efficiency.
#'
#' @inheritParams Gross_From_Net
#' @return Vector of tax amounts.
#' @family Utilities
#' @export
Apply <- function(Amount, Tax_Scale){
  Tax <- 0
  n <- nrow(Tax_Scale)
  for (Th in 1:(n-1)){
    Tax <- Tax + (pmin(pmax(Amount, Tax_Scale[Th, 1]), Tax_Scale[Th + 1, 1]) - Tax_Scale[Th, 1])*Tax_Scale[Th, 2]
  }
  return(Tax + pmax(0, Amount - Tax_Scale[n, 1])*Tax_Scale[n, 2])
}
attr(Apply, "utility") <- T



Gross_From_Net <- function(Amount, Net_Thresholds, Tax_Scale){
  Levels <- length(Net_Thresholds)
  Old <- 0
  Gross <- 0*Amount
  for (Th in 2:Levels){
    Gross <- Gross + (Amount < Net_Thresholds[Th] & Amount >= Net_Thresholds[Th - 1])*
      (Tax_Scale[Th-1, 1] + (Amount - Old) / (1 - Tax_Scale[Th - 1, 2]))
    #print(Tax_Scale[Th-1, 1])
    #print((Amount - Old) / (1 - Tax_Scale[Th - 1, 2]))
    Old <- Net_Thresholds[Th]
  }
  return(Gross + (Amount > Net_Thresholds[Levels]) * 
           (Tax_Scale[Levels, 1] + (Amount - Old) / (1 - Tax_Scale[Levels, 2])))
}
attr(Gross_From_Net, "utility") <- T


#' @title Calculates the net income given gross income and a tax scale.
#' @author Christopher Ball, \email{Christopher.Ball@@treasury.govt.nz}
#'
#' @description This function calculates the net of tax income given the pre-tax
#' amount and the relevant tax scale.  Vectorised for efficiency.
#'
#' @inheritParams Gross_From_Net
#' @return Vector of net of tax income amounts.
#' @seealso Apply
#' @family Utilities
#' @export
Net_From_Gross <- function(Amount, Tax_Scale){
  return(Amount - Apply(Amount, Tax_Scale))
}
attr(Net_From_Gross, "utility") <- T

# Install all the required package for the EMTR function and income explorer app
pkg <- c("shiny", "rhandsontable", "RColorBrewer", "shinythemes",
         "plotly", "openxlsx", "data.table", "ggplot2", 
         "scales", "magrittr", "zoo")

new.pkg <- pkg[!(pkg %in% installed.packages())]

if (length(new.pkg) > 0) {
  install.packages(new.pkg)
}

if(packageVersion("shiny") < "1.5.0"){
  install.packages("shiny")
}

shiny::runApp()
